import React, { useState } from 'react';
import { useCart } from '../contexts/CartContext';

interface RechargeRequestModalProps {
  isOpen: boolean;
  onClose: () => void;
  currentHeartValue: number;
  onSuccess: () => void;
  fromCheckout?: boolean;
  prefilledCustomerInfo?: CustomerInfo | null;
  suggestedAmount?: number;
}

interface CustomerInfo {
  name: string;
  phone: string;
  notes: string;
}

const RechargeRequestModal: React.FC<RechargeRequestModalProps> = ({ 
  isOpen, 
  onClose, 
  currentHeartValue, 
  onSuccess,
  fromCheckout = false,
  prefilledCustomerInfo = null,
  suggestedAmount = 0
}) => {
  const { submitRechargeRequest } = useCart();
  const [amount, setAmount] = useState<string>('');
  const [customerInfo, setCustomerInfo] = useState<CustomerInfo>({
    name: '',
    phone: '',
    notes: '',
  });
  const [loading, setLoading] = useState(false);

  if (!isOpen) return null;

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    const rechargeAmount = parseInt(amount);

    if (!rechargeAmount || rechargeAmount <= 0) {
      alert('ËØ∑ËæìÂÖ•ÊúâÊïàÁöÑÂÖÖÂÄºÈáëÈ¢ù');
      return;
    }

    if (!customerInfo.name || !customerInfo.phone) {
      alert('ËØ∑Â°´ÂÜôÂßìÂêçÂíåËÅîÁ≥ªÁîµËØù');
      return;
    }

    setLoading(true);

    try {
      const success = await submitRechargeRequest(rechargeAmount, customerInfo);

      if (success) {
        alert(`ÂÖÖÂÄºÁî≥ËØ∑Â∑≤Êèê‰∫§ÊàêÂäüÔºÅ\nÁî≥ËØ∑ÈáëÈ¢ùÔºöüíì${rechargeAmount}\nËØ∑Á≠âÂæÖÁÆ°ÁêÜÂëòÂÆ°Ê†∏`);
        setAmount('');
        setCustomerInfo({ name: '', phone: '', notes: '' });
        onSuccess();
      } else {
        alert('Êèê‰∫§Áî≥ËØ∑Â§±Ë¥•ÔºåËØ∑ÈáçËØï');
      }
    } catch (error) {
      alert('Êèê‰∫§Áî≥ËØ∑Â§±Ë¥•ÔºåËØ∑Ê£ÄÊü•ÁΩëÁªúËøûÊé•');
      console.error('Recharge request error:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleAmountChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value;
    // Âè™ÂÖÅËÆ∏ËæìÂÖ•Êï∞Â≠ó
    if (/^\d*$/.test(value)) {
      setAmount(value);
    }
  };

  return (
    <>
      <div className="fixed inset-0 bg-black bg-opacity-50 z-50" onClick={onClose} />
      <div className="fixed inset-0 flex items-center justify-center z-50 p-4">
        <div className="bg-white rounded-lg shadow-xl max-w-md w-full p-6" onClick={(e) => e.stopPropagation()}>
          <h2 className="text-2xl font-bold mb-4 text-pink-600">Áî≥ËØ∑ÂÖÖÂÄºÂøÉÂä®ÂÄº</h2>

          {fromCheckout && (
            <div className="mb-4 bg-red-50 border border-red-200 rounded-lg p-4">
              <p className="text-sm text-red-800">
                <span className="font-semibold">üíî ÂøÉÂä®ÂÄº‰∏çË∂≥ÔºÅ</span>
                <br />
                ÊÇ®ÁöÑÂøÉÂä®ÂÄº‰∏çÂ§üÂÆåÊàêÊ≠§Ê¨°‰∏ãÂçïÔºåËØ∑Áî≥ËØ∑ÂÖÖÂÄºÂêéÂÜçËØï„ÄÇ
              </p>
            </div>
          )}

          <p className="text-gray-600 mb-6">ÂΩìÂâçÂøÉÂä®ÂÄºÔºöüíì{currentHeartValue}</p>

          <form onSubmit={handleSubmit}>
            <div className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  ÂÖÖÂÄºÈáëÈ¢ù * (‰ªÖÈôêÊï∞Â≠ó)
                </label>
                <div className="relative">
                  <input
                    type="text"
                    value={amount}
                    onChange={handleAmountChange}
                    placeholder="ËØ∑ËæìÂÖ•ÂÖÖÂÄºÈáëÈ¢ù"
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:border-pink-500"
                    required
                  />
                  <span className="absolute right-3 top-2 text-pink-500 font-semibold">üíì</span>
                </div>
                {amount && (
                  <p className="text-sm text-gray-500 mt-1">
                    ÂÖÖÂÄºÂêéÂ∞ÜÊã•ÊúâÔºöüíì{currentHeartValue + parseInt(amount || '0')}
                  </p>
                )}
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  ÂßìÂêç *
                </label>
                <input
                  type="text"
                  value={customerInfo.name}
                  onChange={(e) => setCustomerInfo({ ...customerInfo, name: e.target.value })}
                  placeholder="ËØ∑ËæìÂÖ•ÊÇ®ÁöÑÂßìÂêç"
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:border-pink-500"
                  required
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  ËÅîÁ≥ªÁîµËØù *
                </label>
                <input
                  type="tel"
                  value={customerInfo.phone}
                  onChange={(e) => setCustomerInfo({ ...customerInfo, phone: e.target.value })}
                  placeholder="ËØ∑ËæìÂÖ•ÊÇ®ÁöÑËÅîÁ≥ªÁîµËØù"
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:border-pink-500"
                  required
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Â§áÊ≥®ËØ¥Êòé
                </label>
                <textarea
                  value={customerInfo.notes}
                  onChange={(e) => setCustomerInfo({ ...customerInfo, notes: e.target.value })}
                  placeholder="ÂèØÂ°´ÂÜôÂÖÖÂÄºÂéüÂõ†ÊàñÂÖ∂‰ªñËØ¥Êòé"
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:border-pink-500"
                  rows={3}
                />
              </div>
            </div>

            <div className="mt-6 bg-yellow-50 border border-yellow-200 rounded-lg p-4">
              <p className="text-sm text-yellow-800">
                <span className="font-semibold">Ê≥®ÊÑèÔºö</span>
                ÂÖÖÂÄºÁî≥ËØ∑Êèê‰∫§ÂêéÈúÄË¶ÅÁÆ°ÁêÜÂëòÂÆ°Ê†∏ÔºåÂÆ°Ê†∏ÈÄöËøáÂêéÂøÉÂä®ÂÄºÊâç‰ºöÂà∞Ë¥¶„ÄÇËØ∑ËÄêÂøÉÁ≠âÂæÖ„ÄÇ
              </p>
            </div>

            <div className="flex space-x-4 mt-6">
              <button
                type="button"
                onClick={onClose}
                className="flex-1 bg-gray-200 hover:bg-gray-300 text-gray-800 font-bold py-2 px-4 rounded-lg transition-colors duration-200"
                disabled={loading}
              >
                ÂèñÊ∂à
              </button>
              <button
                type="submit"
                className="flex-1 bg-pink-500 hover:bg-pink-600 text-white font-bold py-2 px-4 rounded-lg transition-colors duration-200"
                disabled={loading}
              >
                {loading ? 'Êèê‰∫§‰∏≠...' : 'Êèê‰∫§Áî≥ËØ∑'}
              </button>
            </div>
          </form>
        </div>
      </div>
    </>
  );
};

export default RechargeRequestModal;
