import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { CartProvider } from './contexts/CartContext';
import CategoryList from './components/CategoryList';
import DishCard from './components/DishCard';
import Cart from './components/Cart';
import RechargeRequestModal from './components/RechargeRequestModal';
import { Category, Dish } from './types';
import { useCart } from './contexts/CartContext';

const AppContent: React.FC = () => {
  const [categories, setCategories] = useState<Category[]>([]);
  const [dishes, setDishes] = useState<Dish[]>([]);
  const [selectedCategory, setSelectedCategory] = useState<string>('');
  const [showCart, setShowCart] = useState(false);
  const [showRecharge, setShowRecharge] = useState(false);
  const [rechargeFromCheckout, setRechargeFromCheckout] = useState(false);
  const [prefilledCustomerInfo, setPrefilledCustomerInfo] = useState<any>(null);
  const [suggestedAmount, setSuggestedAmount] = useState<number>(0);
  const { getTotalItems, heartValue, fetchHeartValue, submitRechargeRequest } = useCart();

  useEffect(() => {
    fetchMenu();
  }, []);

  const fetchMenu = async () => {
    try {
      const response = await axios.get('/api/menu');
      const { categories, dishes } = response.data;
      setCategories(categories);
      setDishes(dishes);
      if (categories.length > 0) {
        setSelectedCategory(categories[0].id);
      }
    } catch (error) {
      console.error('Error fetching menu:', error);
    }
  };

  const filteredDishes = dishes.filter(dish => dish.categoryId === selectedCategory);

  return (
    <div className="min-h-screen bg-gray-100">
      {/* Header */}
      <header className="bg-white shadow-md sticky top-0 z-30">
        <div className="flex justify-between items-center px-6 py-4">
          <h1 className="text-2xl font-bold text-orange-600">Kristy‰∏ìÂ±ûÂ§ßÈ•≠Â∫ó</h1>
          <div className="flex items-center space-x-4">
            {/* ÂøÉÂä®ÂÄºÊòæÁ§∫ */}
            <div className="flex items-center bg-pink-100 px-4 py-2 rounded-lg">
              <span className="text-pink-600 font-semibold mr-2">ÂøÉÂä®ÂÄº</span>
              <span className="text-xl font-bold text-pink-600">üíì{heartValue}</span>
              <button
                onClick={() => {
                  setRechargeFromCheckout(false);
                  setShowRecharge(true);
                }}
                className="ml-2 bg-pink-500 hover:bg-pink-600 text-white px-2 py-1 rounded text-sm transition-colors duration-200"
              >
                Áî≥ËØ∑ÂÖÖÂÄº
              </button>
            </div>

            {/* Ë¥≠Áâ©ËΩ¶ÊåâÈíÆ */}
            <button
              onClick={() => setShowCart(true)}
              className="relative bg-orange-500 hover:bg-orange-600 text-white px-4 py-2 rounded-lg transition-colors duration-200"
            >
              <span className="flex items-center">
                <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 3h2l.4 2M7 13h10l4-8H5.4M7 13L5.4 5M7 13l-2.293 2.293c-.63.63-.184 1.707.707 1.707H17m0 0a2 2 0 100 4 2 2 0 000-4zm-8 2a2 2 0 11-4 0 2 2 0 014 0z" />
                </svg>
                Ë¥≠Áâ©ËΩ¶
                {getTotalItems() > 0 && (
                  <span className="ml-2 bg-white text-orange-500 rounded-full w-6 h-6 flex items-center justify-center text-sm font-bold">
                    {getTotalItems()}
                  </span>
                )}
              </span>
            </button>
          </div>
        </div>
      </header>

      <div className="flex">
        {/* Category Sidebar */}
        <CategoryList
          categories={categories}
          selectedCategory={selectedCategory}
          onSelectCategory={setSelectedCategory}
        />

        {/* Main Content */}
        <main className="flex-1 p-6">
          <div className="mb-6">
            <h2 className="text-2xl font-bold text-gray-800">
              {categories.find(c => c.id === selectedCategory)?.name || 'ËèúÂìÅÂàóË°®'}
            </h2>
            <p className="text-gray-600 mt-1">ÂÖ± {filteredDishes.length} ÈÅìËèúÂìÅ</p>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
            {filteredDishes.map((dish) => (
              <DishCard key={dish.id} dish={dish} />
            ))}
          </div>
        </main>
      </div>

      {/* Cart Sidebar */}
      <Cart 
        isOpen={showCart} 
        onClose={() => setShowCart(false)} 
        onOpenRecharge={(customerInfo, neededAmount) => {
          setRechargeFromCheckout(true);
          setPrefilledCustomerInfo(customerInfo);
          setSuggestedAmount(neededAmount || 0);
          setShowRecharge(true);
        }}
      />

      {/* ÂÖÖÂÄºÁî≥ËØ∑Ê®°ÊÄÅÊ°Ü */}
      <RechargeRequestModal 
        isOpen={showRecharge} 
        onClose={() => {
          setShowRecharge(false);
          setRechargeFromCheckout(false);
        }} 
        currentHeartValue={heartValue}
        fromCheckout={rechargeFromCheckout}
        onSuccess={() => {
          setShowRecharge(false);
          setRechargeFromCheckout(false);
          fetchHeartValue(); // Âà∑Êñ∞ÂøÉÂä®ÂÄºÊòæÁ§∫
        }}
      />
    </div>
  );
};

function App() {
  return (
    <CartProvider>
      <AppContent />
    </CartProvider>
  );
}

export default App;
