import React, { useState } from 'react';
import axios from 'axios';
import { useCart } from '../contexts/CartContext';
import { CustomerInfo } from '../types';

interface CheckoutModalProps {
  isOpen: boolean;
  onClose: () => void;
  onSuccess: () => void;
  onOpenRecharge: () => void;
}

const CheckoutModal: React.FC<CheckoutModalProps> = ({ isOpen, onClose, onSuccess, onOpenRecharge }) => {
  const { cartItems, getTotalPrice, heartValue, canAfford, fetchHeartValue } = useCart();
  const [customerInfo, setCustomerInfo] = useState<CustomerInfo>({
    taste: '',
    expectedTime: '',
    notes: '',
  });
  const [loading, setLoading] = useState(false);

  if (!isOpen) return null;

  // Â§ÑÁêÜÂÖÖÂÄºÁî≥ËØ∑Ë∑≥ËΩ¨
  const handleRechargeClick = () => {
    onClose(); // ÂÖ≥Èó≠ÂΩìÂâçÊ®°ÊÄÅÊ°Ü
    onOpenRecharge(); // ÊâìÂºÄheaderÁöÑÂÖÖÂÄºÁî≥ËØ∑Ê®°ÊÄÅÊ°Ü
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    const totalPrice = getTotalPrice();

    // ÂøÉÂä®ÂÄºË∂≥Â§üÊó∂ÊâçÊ£ÄÊü•Ë°®ÂçïÂÆåÊï¥ÊÄß
    if (!customerInfo.taste || !customerInfo.expectedTime) {
      alert('ËØ∑Â°´ÂÜôÂè£Âë≥ÂíåÊúüÊúõÊó∂Èó¥');
      return;
    }

    setLoading(true);

    try {
      const response = await axios.post('/api/orders', {
        items: cartItems,
        total: totalPrice,
        customerInfo,
      });

      if (response.data.success) {
        // ÈáçÊñ∞Ëé∑ÂèñÂøÉÂä®ÂÄºÔºàËÆ¢ÂçïÊàêÂäüÂêéÂêéÁ´Ø‰ºöËá™Âä®Êâ£Èô§Ôºâ
        await fetchHeartValue();
        alert(`ËÆ¢ÂçïÊèê‰∫§ÊàêÂäüÔºÅËÆ¢ÂçïÂè∑Ôºö${response.data.orderId}\nÂ∑≤Êâ£Èô§üíì${totalPrice}ÂøÉÂä®ÂÄº`);
        onSuccess();
      }
    } catch (error) {
      alert('ËÆ¢ÂçïÊèê‰∫§Â§±Ë¥•ÔºåËØ∑ÈáçËØï');
      console.error('Order submission error:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <>
      <div className="fixed inset-0 bg-black bg-opacity-50 z-50" onClick={onClose} />
      <div className="fixed inset-0 flex items-center justify-center z-50 p-4">
        <div className="bg-white rounded-lg shadow-xl max-w-md w-full p-6" onClick={(e) => e.stopPropagation()}>
          <h2 className="text-2xl font-bold mb-4 text-gray-800">Á°ÆËÆ§ËÆ¢Âçï</h2>

          <form onSubmit={handleSubmit}>
            <div className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Âè£Âë≥ *
                </label>
                <input
                  type="text"
                  value={customerInfo.taste}
                  onChange={(e) => setCustomerInfo({ ...customerInfo, taste: e.target.value })}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:border-orange-500"
                  placeholder="Â¶ÇÔºöÂæÆËæ£„ÄÅ‰∏çËæ£„ÄÅÈáçËæ£Á≠â"
                  required
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  ÊúüÊúõÊó∂Èó¥ *
                </label>
                <input
                  type="text"
                  value={customerInfo.expectedTime}
                  onChange={(e) => setCustomerInfo({ ...customerInfo, expectedTime: e.target.value })}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:border-orange-500"
                  placeholder="Â¶ÇÔºö12:30„ÄÅÂ∞ΩÂø´„ÄÅÊôö‰∏ä7ÁÇπÁ≠â"
                  required
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Â§áÊ≥®
                </label>
                <textarea
                  value={customerInfo.notes}
                  onChange={(e) => setCustomerInfo({ ...customerInfo, notes: e.target.value })}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:border-orange-500"
                  placeholder="ÂÖ∂‰ªñË¶ÅÊ±ÇÊàñËØ¥Êòé"
                  rows={3}
                />
              </div>
            </div>

            <div className="mt-6 border-t pt-4">
              {/* ÂøÉÂä®ÂÄº‰ΩôÈ¢ùÊòæÁ§∫ */}
              <div className="flex justify-between items-center mb-2">
                <span className="text-sm text-gray-600">ÂΩìÂâçÂøÉÂä®ÂÄº</span>
                <span className="text-lg font-semibold text-pink-600">
                  üíì{heartValue}
                </span>
              </div>

              <div className="flex justify-between items-center mb-4">
                <span className="text-lg font-semibold">ËÆ¢ÂçïÊÄªËÆ°</span>
                <span className="text-xl font-bold text-orange-500">
                  üíó{getTotalPrice()}
                </span>
              </div>

              {/* ÂøÉÂä®ÂÄºÁä∂ÊÄÅÊèêÁ§∫ */}
              {!canAfford(getTotalPrice()) && (
                <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
                  <p className="text-sm">
                    ‚ùå ÂøÉÂä®ÂÄº‰∏çË∂≥ÔºÅËøòÈúÄË¶Åüíì{getTotalPrice() - heartValue}
                  </p>
                </div>
              )}

              {canAfford(getTotalPrice()) && (
                <div className="bg-green-100 border border-green-400 text-green-700 px-4 py-3 rounded mb-4">
                  <p className="text-sm">
                    ‚úÖ ÂøÉÂä®ÂÄºÂÖÖË∂≥ÔºÅ‰∏ãÂçïÂêéÂâ©‰Ωôüíì{heartValue - getTotalPrice()}
                  </p>
                </div>
              )}
            </div>

            <div className="flex space-x-4">
              <button
                type="button"
                onClick={onClose}
                className="flex-1 bg-gray-200 hover:bg-gray-300 text-gray-800 font-bold py-2 px-4 rounded-lg transition-colors duration-200"
                disabled={loading}
              >
                ÂèñÊ∂à
              </button>
              {!canAfford(getTotalPrice()) ? (
                <button
                  type="button"
                  onClick={handleRechargeClick}
                  className="flex-1 bg-pink-500 hover:bg-pink-600 text-white font-bold py-2 px-4 rounded-lg transition-colors duration-200"
                  disabled={loading}
                >
                  Áî≥ËØ∑ÂÖÖÂÄºüíì
                </button>
              ) : (
                <button
                  type="submit"
                  className={`flex-1 font-bold py-2 px-4 rounded-lg transition-colors duration-200 ${
                    loading
                      ? 'bg-gray-400 cursor-not-allowed'
                      : 'bg-orange-500 hover:bg-orange-600'
                  } text-white`}
                  disabled={loading}
                >
                  {loading ? 'Êèê‰∫§‰∏≠...' : 'Á°ÆËÆ§‰∏ãÂçï'}
                </button>
              )}
            </div>
          </form>
        </div>
      </div>
    </>
  );
};

export default CheckoutModal;
