// API configuration
const config = {
  // 如果设置了环境变量，使用环境变量的值
  // 否则，检查是否在 localtunnel 环境下（通过 URL 判断）
  API_URL: process.env.REACT_APP_API_URL || 
    (window.location.hostname.includes('loca.lt') 
      ? 'https://restaurant-api.loca.lt' 
      : 'http://localhost:3001')
};

export default config;
