import React, { useState } from 'react';
import { useAuth } from '../contexts/AuthContext';

const LoginForm: React.FC = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [adminMode, setAdminMode] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  const { login, register } = useAuth();

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    // Âú®ÁÆ°ÁêÜÂëòÊ®°Âºè‰∏ã‰∏çÂ§ÑÁêÜÊôÆÈÄöË°®ÂçïÊèê‰∫§
    if (adminMode) {
      return;
    }

    setLoading(true);
    setMessage('');

    if (!username.trim() || !password.trim()) {
      setMessage('ËØ∑Â°´ÂÜôÂÆåÊï¥‰ø°ÊÅØ');
      setLoading(false);
      return;
    }

    if (!isLogin && password !== confirmPassword) {
      setMessage('‰∏§Ê¨°ËæìÂÖ•ÁöÑÂØÜÁ†Å‰∏ç‰∏ÄËá¥');
      setLoading(false);
      return;
    }

    if (!isLogin && password.length < 6) {
      setMessage('ÂØÜÁ†ÅÈïøÂ∫¶Ëá≥Â∞ë6‰Ωç');
      setLoading(false);
      return;
    }

    try {
      const result = isLogin 
        ? await login(username, password)
        : await register(username, password);

      if (!result.success) {
        setMessage(result.message);
      }
    } catch (error) {
      setMessage('ÁΩëÁªúÈîôËØØÔºåËØ∑Á®çÂêéÈáçËØï');
    } finally {
      setLoading(false);
    }
  };

  const toggleMode = () => {
    setIsLogin(!isLogin);
    setMessage('');
    setPassword('');
    setConfirmPassword('');
  };

  // ÁÆ°ÁêÜÂëòÁôªÂΩïÂ§ÑÁêÜ
  const handleAdminLogin = async () => {
    setLoading(true);
    setMessage('');

    if (!password) {
      setMessage('ËØ∑ËæìÂÖ•ÂØÜÁ†Å');
      setLoading(false);
      return;
    }

    try {
      // ÂØÜÁ†ÅÊòØkristyÔºåÁî®Êà∑ÂêçÊòØadmin
      if (password === 'kristy') {
        // Áõ¥Êé•‰ΩøÁî®Á°¨ÁºñÁ†ÅÁöÑÁÆ°ÁêÜÂëòÂá≠ÊçÆ (admin/kristy)
        console.log('Â∞ùËØïÁÆ°ÁêÜÂëòÁôªÂΩï: admin/' + password);
        const result = await login('admin', 'kristy');
        console.log('ÁôªÂΩïÁªìÊûú:', result);

        if (!result.success) {
          setMessage(result.message || 'ÁôªÂΩïÂ§±Ë¥•ÔºåËØ∑ËÅîÁ≥ªÁ≥ªÁªüÁÆ°ÁêÜÂëò');
        }
      } else {
        setMessage('ÂØÜÁ†Å‰∏çÊ≠£Á°Æ');
      }
    } catch (error: any) {
      console.error('ÁÆ°ÁêÜÂëòÁôªÂΩïÈîôËØØ:', error);
      setMessage('ÁôªÂΩïÂ§±Ë¥•ÔºåËØ∑Á®çÂêéÈáçËØï (ÈîôËØØ: ' + (error.message || 'Êú™Áü•ÈîôËØØ') + ')');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-orange-100 via-pink-50 to-purple-100 flex items-center justify-center px-4">
      <div className="bg-white rounded-2xl shadow-2xl max-w-md w-full p-8">
        {/* Logo and Title */}
        <div className="text-center mb-8">
          <div className="text-6xl mb-4">üçΩÔ∏è</div>
          <h1 className="text-3xl font-bold text-gray-800 mb-2">Kristy‰∏ìÂ±ûÂ§ßÈ•≠Â∫ó</h1>
          <p className="text-gray-600">
            {isLogin ? 'Ê¨¢ËøéÂõûÊù•ÔºåÂºÄÂßãÊÇ®ÁöÑÁæéÈ£ü‰πãÊóÖ' : 'Ê≥®ÂÜåË¥¶Êà∑Ôºå‰∫´Âèó‰∏ìÂ±ûÁæéÈ£ü‰ΩìÈ™å'}
          </p>
        </div>

        {/* Regular Form (hidden in admin mode) */}
        {!adminMode && (
          <>
            <form onSubmit={handleSubmit} className="space-y-6">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Áî®Êà∑Âêç
                </label>
                <input
                  type="text"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-orange-500 focus:border-orange-500 transition-colors duration-200"
                  placeholder="ËØ∑ËæìÂÖ•Áî®Êà∑Âêç"
                  disabled={loading}
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  ÂØÜÁ†Å
                </label>
                <input
                  type="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-orange-500 focus:border-orange-500 transition-colors duration-200"
                  placeholder="ËØ∑ËæìÂÖ•ÂØÜÁ†Å"
                  disabled={loading}
                />
              </div>

              {!isLogin && (
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Á°ÆËÆ§ÂØÜÁ†Å
                  </label>
                  <input
                    type="password"
                    value={confirmPassword}
                    onChange={(e) => setConfirmPassword(e.target.value)}
                    className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-orange-500 focus:border-orange-500 transition-colors duration-200"
                    placeholder="ËØ∑ÂÜçÊ¨°ËæìÂÖ•ÂØÜÁ†Å"
                    disabled={loading}
                  />
                </div>
              )}

              {message && (
                <div className={`p-3 rounded-lg text-sm ${
                  message.includes('ÊàêÂäü') 
                    ? 'bg-green-100 text-green-700 border border-green-200' 
                    : 'bg-red-100 text-red-700 border border-red-200'
                }`}>
                  {message}
                </div>
              )}

              <button
                type="submit"
                disabled={loading}
                className="w-full bg-gradient-to-r from-orange-500 to-pink-500 hover:from-orange-600 hover:to-pink-600 text-white font-bold py-3 px-4 rounded-lg transition-all duration-300 transform hover:scale-105 disabled:opacity-50 disabled:cursor-not-allowed disabled:transform-none"
              >
                {loading ? (
                  <div className="flex items-center justify-center">
                    <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white mr-2"></div>
                    {isLogin ? 'ÁôªÂΩï‰∏≠...' : 'Ê≥®ÂÜå‰∏≠...'}
                  </div>
                ) : (
                  isLogin ? 'Á´ãÂç≥ÁôªÂΩï' : 'Á´ãÂç≥Ê≥®ÂÜå'
                )}
              </button>
            </form>

            {/* Toggle Mode */}
            <div className="mt-6 text-center">
              <span className="text-gray-600">
                {isLogin ? 'ËøòÊ≤°ÊúâË¥¶Êà∑Ôºü' : 'Â∑≤ÊúâË¥¶Êà∑Ôºü'}
              </span>
              <button
                onClick={toggleMode}
                className="ml-2 text-orange-600 hover:text-orange-700 font-medium transition-colors duration-200"
                disabled={loading}
              >
                {isLogin ? 'Á´ãÂç≥Ê≥®ÂÜå' : 'Á´ãÂç≥ÁôªÂΩï'}
              </button>
            </div>
          </>
        )}

        {/* Admin Hint */}
        {isLogin && !adminMode && (
          <div 
            className="mt-6 p-4 bg-purple-50 border border-purple-200 rounded-lg cursor-pointer hover:bg-purple-100 transition-colors duration-200"
            onClick={() => setAdminMode(true)}
          >
            <div className="text-center">
              <div className="text-2xl mb-2">üëë</div>
              <p className="text-sm text-purple-700">
                <strong>ÁÆ°ÁêÜÂëòÂÖ•Âè£</strong>
              </p>
              <p className="text-xs text-purple-600 mt-1">
                ÁÇπÂáªËøõÂÖ•ÁÆ°ÁêÜÂëòÁôªÂΩï
              </p>
            </div>
          </div>
        )}

        {/* Admin Login Mode */}
        {adminMode && (
          <div className="mt-6 space-y-6">
            <div className="text-center">
              <div className="text-2xl mb-2">üëë</div>
              <h3 className="text-xl font-bold text-purple-700">ÁÆ°ÁêÜÂëòÁôªÂΩï</h3>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                ÁÆ°ÁêÜÂëòÂØÜÁ†Å
              </label>
              <input
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                className="w-full px-4 py-3 border border-purple-300 rounded-lg focus:ring-2 focus:ring-purple-500 focus:border-purple-500 transition-colors duration-200"
                placeholder="ËØ∑ËæìÂÖ•ÁÆ°ÁêÜÂëòÂØÜÁ†Å"
                disabled={loading}
              />
            </div>

            {message && (
              <div className={`p-3 rounded-lg text-sm ${
                message.includes('ÊàêÂäü') 
                  ? 'bg-green-100 text-green-700 border border-green-200' 
                  : 'bg-red-100 text-red-700 border border-red-200'
              }`}>
                {message}
              </div>
            )}

            <div className="flex space-x-4">
              <button
                type="button"
                onClick={() => {
                  setAdminMode(false);
                  setPassword('');
                  setMessage('');
                }}
                className="w-1/2 bg-gray-200 hover:bg-gray-300 text-gray-800 font-bold py-3 px-4 rounded-lg transition-all duration-200"
                disabled={loading}
              >
                ËøîÂõû
              </button>

              <button
                type="button"
                onClick={handleAdminLogin}
                disabled={loading}
                className="w-1/2 bg-gradient-to-r from-purple-500 to-indigo-600 hover:from-purple-600 hover:to-indigo-700 text-white font-bold py-3 px-4 rounded-lg transition-all duration-300 transform hover:scale-105 disabled:opacity-50 disabled:cursor-not-allowed disabled:transform-none"
              >
                {loading ? (
                  <div className="flex items-center justify-center">
                    <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white mr-2"></div>
                    ÁôªÂΩï‰∏≠...
                  </div>
                ) : 'ÁôªÂΩï'}
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default LoginForm;
