import React, { useState, useEffect } from 'react';
import axios from 'axios';

interface Transaction {
  id: number;
  oldValue: number;
  newValue: number;
  changeAmount: number;
  type: 'order' | 'recharge' | 'other';
  description: string;
  relatedId: number | null;
  createdAt: string;
}

interface HeartTransactionsProps {
  isOpen: boolean;
  onClose: () => void;
}

const HeartTransactions: React.FC<HeartTransactionsProps> = ({ isOpen, onClose }) => {
  const [transactions, setTransactions] = useState<Transaction[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (isOpen) {
      loadTransactions();
    }
  }, [isOpen]);

  const loadTransactions = async () => {
    setLoading(true);
    try {
      const response = await axios.get('/api/heart-transactions');
      setTransactions(response.data);
    } catch (error) {
      console.error('Âä†ËΩΩÂøÉÂä®ÂÄºÊµÅÊ∞¥Â§±Ë¥•:', error);
    } finally {
      setLoading(false);
    }
  };

  const getTypeLabel = (type: string) => {
    switch (type) {
      case 'order':
        return 'ËÆ¢ÂçïÊîØ‰ªò';
      case 'recharge':
        return 'ÂÖÖÂÄºÂà∞Ë¥¶';
      default:
        return 'ÂÖ∂‰ªñ';
    }
  };

  const getTypeIcon = (type: string) => {
    switch (type) {
      case 'order':
        return 'üõí';
      case 'recharge':
        return 'üí∞';
      default:
        return 'üìù';
    }
  };

  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    const now = new Date();
    const diffInMs = now.getTime() - date.getTime();
    const diffInHours = diffInMs / (1000 * 60 * 60);
    const diffInDays = diffInHours / 24;

    if (diffInHours < 1) {
      const diffInMinutes = Math.floor(diffInMs / (1000 * 60));
      return `${diffInMinutes}ÂàÜÈíüÂâç`;
    } else if (diffInHours < 24) {
      return `${Math.floor(diffInHours)}Â∞èÊó∂Ââç`;
    } else if (diffInDays < 7) {
      return `${Math.floor(diffInDays)}Â§©Ââç`;
    } else {
      return date.toLocaleDateString('zh-CN', { 
        year: 'numeric', 
        month: 'short', 
        day: 'numeric',
        hour: '2-digit',
        minute: '2-digit'
      });
    }
  };

  if (!isOpen) return null;

  return (
    <>
      <div className="fixed inset-0 bg-black bg-opacity-50 z-50" onClick={onClose} />
      <div className="fixed inset-0 flex items-center justify-center z-50 p-4">
        <div className="bg-white rounded-lg shadow-xl max-w-2xl w-full max-h-[80vh] overflow-hidden" onClick={(e) => e.stopPropagation()}>
          <div className="p-6 border-b border-gray-200">
            <div className="flex justify-between items-center">
              <h2 className="text-2xl font-bold text-pink-600">üíì ÂøÉÂä®ÂÄºÊµÅÊ∞¥ËÆ∞ÂΩï</h2>
              <button
                onClick={onClose}
                className="text-gray-400 hover:text-gray-600 text-2xl font-bold"
              >
                √ó
              </button>
            </div>
            <p className="text-gray-600 mt-2">Êü•ÁúãÊÇ®ÁöÑÂøÉÂä®ÂÄºÂèòÂä®ÂéÜÂè≤</p>
          </div>

          <div className="p-6 overflow-y-auto max-h-[calc(80vh-160px)]">
            {loading ? (
              <div className="text-center py-8">
                <div className="inline-block animate-spin rounded-full h-8 w-8 border-b-2 border-pink-500"></div>
                <p className="mt-2 text-gray-600">Âä†ËΩΩ‰∏≠...</p>
              </div>
            ) : transactions.length === 0 ? (
              <div className="text-center py-8">
                <div className="text-6xl mb-4">üí∏</div>
                <p className="text-gray-600">ÊöÇÊó†ÂøÉÂä®ÂÄºÂèòÂä®ËÆ∞ÂΩï</p>
              </div>
            ) : (
              <div className="space-y-4">
                {transactions.map((transaction) => (
                  <div
                    key={transaction.id}
                    className={`p-4 rounded-lg border-l-4 ${
                      transaction.changeAmount > 0
                        ? 'border-green-500 bg-green-50'
                        : 'border-red-500 bg-red-50'
                    }`}
                  >
                    <div className="flex items-center justify-between">
                      <div className="flex items-center space-x-3">
                        <span className="text-2xl">{getTypeIcon(transaction.type)}</span>
                        <div>
                          <div className="font-semibold text-gray-800">
                            {getTypeLabel(transaction.type)}
                          </div>
                          <div className="text-sm text-gray-600">
                            {transaction.description}
                          </div>
                        </div>
                      </div>
                      <div className="text-right">
                        <div className={`text-lg font-bold ${
                          transaction.changeAmount > 0 ? 'text-green-600' : 'text-red-600'
                        }`}>
                          {transaction.changeAmount > 0 ? '+' : ''}üíì{transaction.changeAmount}
                        </div>
                        <div className="text-sm text-gray-500">
                          ‰ΩôÈ¢ù: üíì{transaction.newValue}
                        </div>
                      </div>
                    </div>
                    <div className="mt-2 text-xs text-gray-500 flex justify-between">
                      <span>ÊµÅÊ∞¥Âè∑: #{transaction.id}</span>
                      <span>{formatDate(transaction.createdAt)}</span>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>

          <div className="p-6 border-t border-gray-200 bg-gray-50">
            <div className="flex justify-between items-center">
              <div className="text-sm text-gray-600">
                <span className="inline-flex items-center mr-4">
                  <span className="w-3 h-3 bg-green-500 rounded-full mr-2"></span>
                  Êî∂ÂÖ•
                </span>
                <span className="inline-flex items-center">
                  <span className="w-3 h-3 bg-red-500 rounded-full mr-2"></span>
                  ÊîØÂá∫
                </span>
              </div>
              <button
                onClick={onClose}
                className="bg-gray-200 hover:bg-gray-300 text-gray-800 font-bold py-2 px-4 rounded-lg transition-colors duration-200"
              >
                ÂÖ≥Èó≠
              </button>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default HeartTransactions;
