import React, { useState } from 'react';
import { useAuth } from '../contexts/AuthContext';

const LoginForm: React.FC = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [adminMode, setAdminMode] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  const { login, register } = useAuth();

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    // Âú®ÁÆ°ÁêÜÂëòÊ®°Âºè‰∏ã‰∏çÂ§ÑÁêÜÊôÆÈÄöË°®ÂçïÊèê‰∫§
    if (adminMode) {
      return;
    }

    setLoading(true);
    setMessage('');

    if (!username.trim() || !password.trim()) {
      setMessage('ËØ∑Â°´ÂÜôÂÆåÊï¥‰ø°ÊÅØ');
      setLoading(false);
      return;
    }

    if (!isLogin && password !== confirmPassword) {
      setMessage('‰∏§Ê¨°ËæìÂÖ•ÁöÑÂØÜÁ†Å‰∏ç‰∏ÄËá¥');
      setLoading(false);
      return;
    }

    if (!isLogin && password.length < 6) {
      setMessage('ÂØÜÁ†ÅÈïøÂ∫¶Ëá≥Â∞ë6‰Ωç');
      setLoading(false);
      return;
    }

    try {
      const result = isLogin 
        ? await login(username, password)
        : await register(username, password);

      if (!result.success) {
        setMessage(result.message);
      }
    } catch (error) {
      setMessage('ÁΩëÁªúÈîôËØØÔºåËØ∑Á®çÂêéÈáçËØï');
    } finally {
      setLoading(false);
    }
  };

  const toggleMode = () => {
    setIsLogin(!isLogin);
    setMessage('');
    setPassword('');
    setConfirmPassword('');
  };

  // ÁÆ°ÁêÜÂëòÁôªÂΩïÂ§ÑÁêÜ
  const handleAdminLogin = async () => {
    setLoading(true);
    setMessage('');

    if (!password) {
      setMessage('ËØ∑ËæìÂÖ•ÂØÜÁ†Å');
      setLoading(false);
      return;
    }

    try {
      // ÂØÜÁ†ÅÊòØkristyÔºåÁî®Êà∑ÂêçÊòØadmin
      if (password === 'kristy') {
        // Áõ¥Êé•‰ΩøÁî®Á°¨ÁºñÁ†ÅÁöÑÁÆ°ÁêÜÂëòÂá≠ÊçÆ (admin/kristy)
        console.log('Â∞ùËØïÁÆ°ÁêÜÂëòÁôªÂΩï: admin/' + password);
        const result = await login('admin', 'kristy');
        console.log('ÁôªÂΩïÁªìÊûú:', result);

        if (!result.success) {
          setMessage(result.message || 'ÁôªÂΩïÂ§±Ë¥•ÔºåËØ∑ËÅîÁ≥ªÁ≥ªÁªüÁÆ°ÁêÜÂëò');
        }
      } else {
        setMessage('ÂØÜÁ†Å‰∏çÊ≠£Á°Æ');
      }
    } catch (error: any) {
      console.error('ÁÆ°ÁêÜÂëòÁôªÂΩïÈîôËØØ:', error);
      setMessage('ÁôªÂΩïÂ§±Ë¥•ÔºåËØ∑Á®çÂêéÈáçËØï (ÈîôËØØ: ' + (error.message || 'Êú™Áü•ÈîôËØØ') + ')');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-orange-100 via-pink-50 to-purple-100 flex items-center justify-center px-4">
      <div className="bg-white rounded-2xl shadow-2xl max-w-md w-full p-8">
        {/* Logo and Title */}
        <div className="text-center mb-8">
          <div className="text-6xl mb-4">üçΩÔ∏è</div>
          <h1 className="text-3xl font-bold text-gray-800 mb-2">Kristy‰∏ìÂ±ûÂ§ßÈ•≠Â∫ó</h1>
          <p className="text-gray-600">
            {isLogin ? 'Ê¨¢ËøéÂõûÊù•ÔºåÂºÄÂßãÊÇ®ÁöÑÁæéÈ£ü‰πãÊóÖ' : 'Ê≥®ÂÜåË¥¶Êà∑Ôºå‰∫´Âèó‰∏ìÂ±ûÁæéÈ£ü‰ΩìÈ™å'}
          </p>
        </div>

        {/* Regular Form (hidden in admin mode) */}
        {!adminMode && (
          <>
            <form onSubmit={handleSubmit} className="space-y-6">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Áî®Êà∑Âêç
                </label>
                <input
                  type="text"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-orange-500 focus:border-orange-500 transition-colors duration-200"
                  placeholder="ËØ∑ËæìÂÖ•Áî®Êà∑Âêç"
                  disabled={loading}
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  ÂØÜÁ†Å
                </label>
                <input
                  type="password"
                  value={password}
